package LeftShift;

public class LeftShiftConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		
		int num = 32;
		
		num = num << 5;
		System.out.println(num);
		
		num = num >> 1;
		
		System.out.println(num);
		
		
		
	int x = 5;
	
	x = x << 3;
	
	System.out.println(x);
		
	
	x = x >> 3;
	System.out.println(x);
		
		
		
		

	}

}
